import React, { useEffect, useRef, useState } from "react";

/**
 * Chatbot component
 * - POSTS { message, userLang } to backend /api/chat
 * - expects JSON { reply: "...", debug?: { ... } }
 *
 * If you use CRA and set "proxy": "http://localhost:5000" in client/package.json
 * you can keep API_BASE = '' and the dev server will proxy requests.
 */
const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:5000";

export default function Chatbot() {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hola — soy tu asistente. ¿Cómo puedo ayudarte?" },
  ]);
  const [input, setInput] = useState("");
  const [lang, setLang] = useState("en");
  const [loading, setLoading] = useState(false);
  const [debug, setDebug] = useState(null);
  const endRef = useRef(null);

  // auto-scroll on new message
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const addMessage = (m) =>
    setMessages((prev) => {
      // keep last 200 messages to avoid memory blow-up
      const next = [...prev, m].slice(-200);
      return next;
    });

  async function sendMessage() {
    const text = input.trim();
    if (!text) return;
    addMessage({ sender: "user", text, ts: Date.now() });
    setInput("");
    setLoading(true);
    setDebug(null);

    try {
      const res = await fetch(`${API_BASE}/api/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: text, userLang: lang }),
      });

      if (!res.ok) {
        // try to read text error for helpful message
        const errText = await res.text();
        throw new Error(errText || `HTTP ${res.status}`);
      }

      const data = await res.json();
      const reply = data.reply || "No response from server.";
      addMessage({ sender: "bot", text: reply, ts: Date.now() });

      if (data.debug) setDebug(data.debug);
    } catch (err) {
      console.error("Chat error:", err);
      addMessage({ sender: "bot", text: `⚠️ Error: ${err.message}` });
    } finally {
      setLoading(false);
    }
  }

  function onEnter(e) {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      if (!loading) sendMessage();
    }
  }

  return (
    <div className="flex flex-col w-full max-w-2xl mx-auto h-[80vh] bg-white rounded-xl shadow-lg overflow-hidden">
      {/* header */}
      <div className="flex items-center justify-between px-4 py-3 bg-indigo-600 text-white">
        <div className="flex items-center gap-3">
          <div className="w-9 h-9 rounded-full bg-indigo-800 flex items-center justify-center">🤖</div>
          <div className="font-semibold">Multilingual Chatbot</div>
        </div>
        <div className="text-sm opacity-90">Model: Ollama / {process.env.REACT_APP_MODEL || "llama3"}</div>
      </div>

      {/* messages */}
      <div className="flex-1 p-4 overflow-y-auto space-y-3 bg-gray-50">
        {messages.map((m, i) => (
          <div key={i} className={`flex ${m.sender === "user" ? "justify-end" : "justify-start"}`}>
            <div
              className={`px-4 py-2 rounded-2xl max-w-[80%] break-words ${
                m.sender === "user" ? "bg-blue-500 text-white" : "bg-white text-gray-900 border"
              }`}
            >
              {m.text}
            </div>
          </div>
        ))}

        {loading && (
          <div className="text-sm text-gray-500 italic">🤖 Bot is typing...</div>
        )}

        <div ref={endRef} />
      </div>

      {/* input */}
      <div className="px-4 py-3 border-t bg-white">
        <div className="flex gap-2 items-center">
          <select
            value={lang}
            onChange={(e) => setLang(e.target.value)}
            className="border rounded-md px-2 py-1"
            aria-label="language"
          >
            <option value="en">English</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
            <option value="de">German</option>
            <option value="hi">Hindi</option>
          </select>

          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={onEnter}
            placeholder="Type your message and press Enter to send"
            className="flex-1 border rounded-md px-3 py-2 h-10 resize-none focus:outline-none"
            disabled={loading}
          />

          <button
            onClick={sendMessage}
            disabled={loading}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 disabled:opacity-60"
          >
            Send
          </button>
        </div>

        {debug && (
          <pre className="mt-2 text-xs text-gray-600 bg-gray-100 p-2 rounded">
            {JSON.stringify(debug, null, 2)}
          </pre>
        )}
      </div>
    </div>
  );
}
